import styles from './Counter.module.css'
import { useSelector, useDispatch } from 'react-redux'


export default function Counter(){
    const count = useSelector ((state) => state.counter.value)

    return (
        <div>
            <div className={styles.row}>
                <button className={styles.button} aria-label="Decrement value"> - </button>
                    <span className={styles.value}>:</span>
                <button className={styles.button} aria-label="Increment value" > + </button>
            </div>
            <div className={styles.row}>
                <input className={styles.textbox} aria-label="Set increment amount" value="{incrementAmount}"/>
                <button className={styles.button}> Add Amount </button>
                <button
                    className={styles.asyncButton}
                    // onClick={}
                >
                    Add Async
                </button>
                <button
                    className={styles.button}
                    // onClick={}
                >
                    Add If Odd
                </button>
            </div>
        </div>
    )
}
